-- 5. POLÍTICAS DE SEGURANÇA
-- Utiliza acesso direto ao JWT para verificação de role, que é mais fiável.
CREATE POLICY "Admins can manage contacts" ON public.contacts FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Users can view their own contact info" ON public.contacts FOR SELECT USING ( id = (SELECT contact_id FROM public.profiles WHERE id = auth.uid()) );
CREATE POLICY "Users can update their own contact info" ON public.contacts FOR UPDATE USING ( id = (SELECT contact_id FROM public.profiles WHERE id = auth.uid()) );
CREATE POLICY "Users can view their own profile" ON public.profiles FOR SELECT USING ( auth.uid() = id );
CREATE POLICY "Admins can view all profiles" ON public.profiles FOR SELECT USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Public can read products" ON public.products FOR SELECT USING (true);
CREATE POLICY "Public can read categories" ON public.categories FOR SELECT USING (true);
CREATE POLICY "Admins can manage products" ON public.products FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Admins can manage categories" ON public.categories FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Users can manage their own orders" ON public.orders FOR ALL USING ( contact_id = (SELECT contact_id FROM public.profiles WHERE id = auth.uid()) );
CREATE POLICY "Staff can view all orders" ON public.orders FOR SELECT USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') IN ('admin', 'caixa', 'cozinha') );
CREATE POLICY "Staff can update orders" ON public.orders FOR UPDATE USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') IN ('admin', 'caixa', 'cozinha') );
CREATE POLICY "Users can manage their own order items" ON public.order_items FOR ALL USING ( order_id IN (SELECT id FROM public.orders WHERE contact_id = (SELECT contact_id FROM public.profiles WHERE id = auth.uid())) );
CREATE POLICY "Staff can view all order items" ON public.order_items FOR SELECT USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') IN ('admin', 'caixa', 'cozinha') );
CREATE POLICY "Public can read store settings" ON public.store_settings FOR SELECT USING (true);
CREATE POLICY "Admins can update store settings" ON public.store_settings FOR UPDATE USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Admins can insert store settings" ON public.store_settings FOR INSERT WITH CHECK ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Public can read active promotions" ON public.promotions FOR SELECT USING (is_active = true);
CREATE POLICY "Admins can manage promotions" ON public.promotions FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to connections" ON public.whatsapp_connections FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to chats" ON public.whatsapp_chats FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to messages" ON public.whatsapp_messages FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to whatsapp settings" ON public.whatsapp_settings FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to chatbot flows" ON public.chatbot_flows FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to chatbot nodes" ON public.chatbot_nodes FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );
CREATE POLICY "Allow admin access to chatbot edges" ON public.chatbot_edges FOR ALL USING ( (auth.jwt() -> 'raw_user_meta_data' ->> 'role') = 'admin' );

