ALTER PUBLICATION supabase_realtime ADD TABLE public.whatsapp_chats;
ALTER PUBLICATION supabase_realtime ADD TABLE public.whatsapp_messages;
CREATE OR REPLACE VIEW public.chats_with_profile AS SELECT wc.id, COALESCE(c.full_name, wc.name) AS display_name, wc.last_message_timestamp, wc.status, wc.created_at, c.profile_picture_url FROM public.whatsapp_chats wc LEFT JOIN public.contacts c ON (regexp_replace(wc.id, '[^0-9]', '', 'g')) = c.phone;